import React, { Component } from "react";

interface CalendarComponentProps {
}

interface CalendarComponentState {
  currentMonth: Date,
  selectedDate: Date
}

export default class CalendarComponent extends Component<CalendarComponentProps, CalendarComponentState> {
  constructor(props) {
    super(props);
    this.state = {
      currentMonth: new Date(),
      selectedDate: new Date()
    };
  }

  onDateClick(day) {}

  nextMonth() {
    this.setState({
      // currentMonth: moment().add(1, this.state.currentMonth)
    });
  }

  prevMonth() {}

  renderHeader() {
    const dateFormat = "MMMM YYYY";

    return (
      <div className="header row flex-middle">
        <div className="col col-start">
          <div className="icon" onClick={this.prevMonth}>
            chevron_left
          </div>
        </div>
        <div className="col col-center">
          <span>{moment().format(this.state.currentMonth && dateFormat)}</span>
        </div>
        <div className="col col-end" onClick={this.nextMonth}>
          <div className="icon">chevron_right</div>
        </div>
      </div>
    );
  }
  renderDays() {}
  renderCells() {}

  render() {
    return (
      <div>
        {this.renderHeader()}
        {this.renderDays()}
        {this.renderCells()}
      </div>
    );
  }
}
